SELECT tlal.user, CASE WHEN MIN(tlal.create_time) BETWEEN CONCAT(#{startDate},' 00:00:00') and CONCAT(#{endDate},' 23:59:59') THEN '新访客' ELSE '老访客' END AS uvType FROM t_link tl INNER JOIN t_link_access_logs tlal ON tl.full_short_url = tlal.full_short_url WHERE tlal.full_short_url = #{fullShortUrl} AND tl.gid = #{gid} AND tl.del_flag = '0' AND tl.enable_status = #{enableStatus} AND tlal.user IN
#{item}
GROUP BY tlal.user

